/* eslint-disable react/prop-types */
import { useMutation } from '@apollo/client';
import gql from 'graphql-tag';

const DELETE_ITEM_MUTATION = gql`
  mutation DELETE_ITEM_MUTATION($id: ID!) {
    deleteCartItem(id: $id) {
      id
    }
  }
`;

export default function DeleteItem({ cartItem }) {
  const [deleteCartItem, { loading }] = useMutation(DELETE_ITEM_MUTATION, {
    variables: {
      id: cartItem.id,
    },
  });
  return (
    <button
      style={{
        background: 'none',
        color: 'red',
        border: 'none',
        paddingLeft: '1rem',
        fontSize: '2rem',
      }}
      disabled={loading}
      onClick={deleteCartItem}
      type="button"
    >
      &times;
    </button>
  );
}
