import useForm from '../lib/useForm';

export default function CreateProduct() {
  const { inputs, handleChange } = useForm();
  return (
    <form>
      <label htmlFor="name">
        Name
        <input
          type="text"
          id="name"
          name="name"
          placeholder="Name"
          value={name}
          onChange={handleChange}
        />
      </label>
      <label htmlFor="price">
        Price
        <input
          type="text"
          id="price"
          name="price"
          placeholder="price"
          value={price}
          onChange={handleChange}
        />
      </label>
    </form>
  );
}
